mapper.select:
select  d1.department_id,d1.department_name,d1.parent_id,d2.*
from t_department d1  join t_department d2 on d1.department_id = d2.parent_id  where d1.department_id = #{parent_id}
order by d2.order_num

mapper.insert:

insert into t_department (department_name, manager_id, parent_id, 
  dept_level, description, `status`, 
  is_deleted, create_by, update_by, 
  create_time, update_time, order_num
  )
values (#{departmentName,jdbcType=VARCHAR}, #{managerId,jdbcType=BIGINT}, #{parentId,jdbcType=BIGINT}, 
  #{deptLevel,jdbcType=TINYINT}, #{description,jdbcType=VARCHAR}, #{status,jdbcType=TINYINT}, 
  #{isDeleted,jdbcType=TINYINT}, #{createBy,jdbcType=BIGINT}, #{updateBy,jdbcType=BIGINT}, 
  #{createTime,jdbcType=TIMESTAMP}, #{updateTime,jdbcType=TIMESTAMP}, #{orderNum,jdbcType=INTEGER}
  )

mapper.select:

select

from t_department


        and department_id=#{departmentId,jdbcType=BIGINT}


        and department_name=#{departmentName,jdbcType=VARCHAR}


        and manager_id=#{managerId,jdbcType=BIGINT}


        and parent_id=#{parentId,jdbcType=BIGINT}


        and dept_level=#{deptLevel,jdbcType=TINYINT}


        and description='%'#{description,jdbcType=VARCHAR}'%'


        and `status`=#{status,jdbcType=TINYINT}


        and is_deleted=#{isDeleted,jdbcType=TINYINT}


        and create_by=#{createBy,jdbcType=BIGINT}


        and update_by=#{updateBy,jdbcType=BIGINT}


        and create_time=#{createTime,jdbcType=TIMESTAMP}


        and update_time=#{updateTime,jdbcType=TIMESTAMP}


        and order_num=#{orderNum,jdbcType=INTEGER}


        and icon_class=#{iconClass,jdbcType=VARCHAR}

mapper.update:

update t_department




        when department_id = #{item.departmentId,jdbcType=BIGINT} then #{item.departmentName,jdbcType=VARCHAR}






