mapper.select:

select

from t_notification


        and notification_id=#{notificationId,jdbcType=BIGINT}


        and user_id=#{userId,jdbcType=BIGINT}


        and ticket_id=#{ticketId,jdbcType=BIGINT}


        and notify_type=#{notifyType,jdbcType=TINYINT}


        and content=#{content,jdbcType=LONGVARCHAR}


        and is_read=#{isRead,jdbcType=TINYINT}


        and is_deleted=#{isDeleted,jdbcType=TINYINT}


        and create_time=#{createTime,jdbcType=TIMESTAMP}

mapper.update:

update t_notification




        when notification_id = #{item.notificationId,jdbcType=BIGINT} then #{item.userId,jdbcType=BIGINT}






        when notification_id = #{item.notificationId,jdbcType=BIGINT} then #{item.ticketId,jdbcType=BIGINT}






        when notification_id = #{item.notificationId,jdbcType=BIGINT} then #{item.notifyType,jdbcType=TINYINT}






        when notification_id = #{item.notificationId,jdbcType=BIGINT} then #{item.content,jdbcType=LONGVARCHAR}






        when notification_id = #{item.notificationId,jdbcType=BIGINT} then #{item.isRead,jdbcType=TINYINT}






        when notification_id = #{item.notificationId,jdbcType=BIGINT} then #{item.isDeleted,jdbcType=TINYINT}






        when notification_id = #{item.notificationId,jdbcType=BIGINT} then #{item.createTime,jdbcType=TIMESTAMP}




where notification_id in

  #{item.notificationId,jdbcType=BIGINT}
