sql.where:
t.is_deleted = 0


        AND (
        t.title LIKE CONCAT('%', #{query.keyword}, '%')
        OR t.content LIKE CONCAT('%', #{query.keyword}, '%')
        OR t.ticket_id LIKE CONCAT('%', #{query.keyword}, '%')
        )


        AND t.status = #{query.status}


        AND t.priority = #{query.priority}


        AND t.department_id = #{query.departmentId}


        AND t.processor_id = #{query.processorId}


        AND (t.create_by = #{query.userId} OR t.processor_id = #{query.userId})


        AND t.create_time >= #{query.startTime}


        AND t.create_time <= #{query.endTime}

mapper.select:
SELECT t.*, tt.type_name as typeName, d.department_name as departmentName, u.real_name as processorName FROM t_ticket t LEFT JOIN t_ticket_type tt ON t.type_id = tt.type_id LEFT JOIN t_department d ON t.department_id = d.department_id LEFT JOIN t_user u ON t.processor_id = u.user_id WHERE t.is_deleted = 0
AND t.create_by = #{query.userId}
AND ( t.title LIKE CONCAT('%', #{query.keyword}, '%') OR t.content LIKE CONCAT('%', #{query.keyword}, '%') OR t.ticket_id LIKE CONCAT('%', #{query.keyword}, '%'))
AND t.status = #{query.status}
AND t.priority = #{query.priority}
AND t.create_time >= #{query.startTime}
AND t.create_time <= #{query.endTime}
AND t.department_id = #{query.departmentId}
ORDER BY CASE t.status WHEN 2 THEN 3 WHEN 3 THEN 4 ELSE 1 END ASC, CASE t.priority WHEN 2 THEN 1 WHEN 1 THEN 2 ELSE 3 END ASC, t.create_time ASC

mapper.insert:
INSERT INTO t_ticket (
type_id, title, content, processor_id, department_id,
priority, status, expect_finish_time, create_by,
create_time, is_deleted
) VALUES (
#{typeId}, #{title}, #{content}, #{processorId}, #{departmentId},
#{priority}, #{status}, #{expectFinishTime}, #{createBy},
#{createTime}, #{isDeleted}
)

mapper.select:
SELECT t_ticket.*, t_department.department_name , t_ticket_type.type_name FROM t_ticket left join t_department on t_ticket.department_id = t_department.department_id left join t_ticket_type on t_ticket.type_id = t_ticket_type.type_id WHERE processor_id = #{queryDTO.processorId,jdbcType=BIGINT} AND t_ticket.status IN (0, 1) AND t_ticket.is_deleted = 0
AND ( t_ticket.title LIKE CONCAT('%', #{queryDTO.keyword}, '%') OR t_ticket.content LIKE CONCAT('%', #{queryDTO.keyword}, '%') )
AND t_ticket.priority = #{queryDTO.priority}
AND t_ticket.department_id = #{queryDTO.departmentId}
AND t_ticket.create_time >= #{queryDTO.startTime}
AND t_ticket.create_time <= #{queryDTO.endTime}
ORDER BY CASE t_ticket.status WHEN 0 THEN 0 WHEN 1 THEN 1 END, t_ticket.create_time ASC, t_ticket.priority DESC

SELECT COUNT(1)
FROM t_ticket t
WHERE t.is_deleted = 0

    AND (
    t.title LIKE CONCAT('%', #{query.keyword}, '%')
    OR t.content LIKE CONCAT('%', #{query.keyword}, '%')
    )


    AND t.status = #{query.status}


    AND t.priority = #{query.priority}


    AND t.department_id = #{query.departmentId}


    AND t.processor_id = #{query.processorId}


    AND t.create_time >= #{query.startTime}


    AND t.create_time <= #{query.endTime}

mapper.update:
UPDATE t_ticket SET status = #{status}, update_by = #{updateBy}, update_time = NOW(), actual_finish_time = CASE WHEN #{status} IN (2,3) THEN NOW() ELSE actual_finish_time END WHERE ticket_id IN
#{ticketId}
AND is_deleted = 0

mapper.select:
select count(1)
from t_ticket
where is_deleted = 0
  and processor_id = #{userId}
  and status = 1
HAVING count(1) <= #{workloadFilter}

SELECT COUNT(1)
FROM t_ticket
WHERE processor_id = #{userId}
AND status = 2
AND actual_finish_time >= #{todayStart}
AND is_deleted = 0

SELECT COUNT(1)
FROM t_ticket
WHERE processor_id = #{userId}
AND status = #{status,typeHandler=com.icss.etc.ticket.handler.TicketStatusTypeHandler}
AND is_deleted = 0

SELECT COUNT(1)
FROM t_ticket
WHERE processor_id = #{userId}
AND status = 2
AND update_time BETWEEN #{startTime} AND #{endTime}
AND is_deleted = 0

SELECT COUNT(1)
FROM t_ticket
WHERE department_id = #{departmentId}
AND status = #{status}
AND is_deleted = 0
