mapper.select:
select
      ticket_id,
      type_id,
      title,
      content,
      processor_id,
      department_id,
      priority,
      status,
      expect_finish_time,
      actual_finish_time,
      is_deleted,
      create_by,
      update_by,
      create_time ,
      update_time
from t_ticket
where is_deleted = 0

    and type_id = #{type_id,jdbcType=BIGINT}


    and title = #{title,jdbcType=VARCHAR}


    and content = #{content,jdbcType=VARCHAR}


    and processor_id = #{processor_id,jdbcType=BIGINT}


    and department_id = #{department_id,jdbcType=BIGINT}


    and priority = #{priority,jdbcType=TINYINT}


    and `status` = #{status,jdbcType=TINYINT}


    and expect_finish_time = #{expect_finish_time,jdbcType=TIMESTAMP}


    and actual_finish_time = #{actual_finish_time,jdbcType=TIMESTAMP}


    and create_by = #{create_by,jdbcType=BIGINT}


    and update_by = #{update_by,jdbcType=BIGINT}


    and create_time = #{create_time,jdbcType=TIMESTAMP}


    and update_time = #{update_time,jdbcType=TIMESTAMP}

order by create_time desc

mapper.update:
update t_ticket
set is_deleted = 1
where ticket_id = #{ticket_id,jdbcType=BIGINT}
