        when user_id = #{item.userId,jdbcType=BIGINT} then #{item.updateTime,jdbcType=TIMESTAMP}




where user_id in

  #{item.userId,jdbcType=BIGINT}

update t_user
set department_id = #{departmentId,jdbcType=BIGINT}
where user_id = #{userId,jdbcType=BIGINT}
  and is_deleted = 0
  and status = 1
